import Resizeable, { ResizeableHolder } from '@renderer/common/resizeable'
import Zoomable from '@renderer/common/zoomable'
import { Editor } from '@tiptap/react'
import { Node } from 'prosemirror-model'
import { NodeView, EditorView } from 'prosemirror-view'
import { v4 as uuidv4 } from 'uuid'

export class ImageView implements NodeView {
  node: Node

  dom: HTMLElement

  img: HTMLImageElement

  view: EditorView

  getPos: () => number

  width: string | number | null

  height: string | number | null

  private id: string

  private resizeableHolder: ResizeableHolder

  constructor(node: Node, editor: Editor, getPos: () => number) {
    const { view } = editor
    this.node = node
    this.view = view
    this.getPos = getPos
    this.width = node.attrs.width
    this.height = node.attrs.height
    this.id = node.attrs.id ? node.attrs.id : uuidv4()

    this.img = document.createElement('img')
    this.img.onerror = (): void => {
      this.img.src =
        'data:image/png;base64,'
    }
    const base = editor.options.editorProps.attributes.contentFilePath || ''
    const src = node.attrs.src
    if (
      src.startsWith('http') ||
      src.startsWith('https') ||
      src.startsWith('ws://') ||
      src.startsWith('wss://') ||
      src.startsWith('data:')
    ) {
      this.img.src = src
    } else {
      window.electron.ipcRenderer.invoke('get-absolute-path', base, '../').then((realBase) => {
        window.electron.ipcRenderer.invoke('get-absolute-path', realBase, src).then((result) => {
          this.img.src = 'app:///' + result
        })
      })
    }
    this.img.alt = node.attrs.alt
    this.img.style.width = '100%'
    this.img.style.height = '100%'

    this.resizeableHolder = Resizeable.wrap(
      Zoomable.wrap(this.img),
      (width, height) => {
        const { state, dispatch } = this.view
        const attrs = {
          src: this.node.attrs.src,
          alt: '',
          id: this.id,
          width: width,
          height: height
        }

        const transaction = state.tr.setNodeMarkup(this.getPos(), undefined, attrs)
        state.tr.setNodeMarkup(this.getPos(), undefined, attrs)
        dispatch(transaction)
      },
      this.width,
      this.height
    )
    this.dom = this.resizeableHolder.container
  }

  update(
    node: Node,
    decorations: readonly Decoration[],
    innerDecorations: DecorationSource
  ): boolean {
    if (node.type !== this.node.type) {
      return false
    }
    if (node.attrs.src !== this.node.attrs.src) {
      return false
    }
    if (!node.attrs.id || node.attrs.id !== this.id) {
      return false
    }
    this.node = node
    return true
  }

  ignoreMutation(): boolean {
    return true
  }

  destroy(): void {
    this.resizeableHolder.destroy()
  }
}
